import React, { useState } from 'react'
import Card from '../UI/Card';
import classes from "./AddUser.module.css"
import Button from '../UI/Button';

const AddUser = (props) => {
    let defaultUserName = " ";
    let defaultUserAge = " ";
    const [enteredUserName, setUserName] = useState(' ')

    const [enteredUserAge, setUserAge] = useState(' ')

    const addUserHandler = (event) => {
        event.preventDefault();
        console.log(enteredUserName, enteredUserAge)



    }

    const userNameChangeHandler = (event) => {
        setUserName(event.target.value)
        // console.log(enteredUserName, setUserName)

    }
    const ageChangeHandler = (event) => {
        setUserAge(event.target.value)
        // console.log(enteredUserAge, setUserAge)

    }

    return (
        <>
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor='username'>UserName </label>
                    <input id="username" type="text" value={enteredUserName} onChange={userNameChangeHandler} />
                    <label htmlFor='username' >UserName(Years) </label>
                    <input id="username" type="number" value={enteredUserAge} onChange={ageChangeHandler} />
                    <Button type='submit'>Add User</Button>

                </form>
            </Card>

        </>
    )
}

export default AddUser;
