import Card from "../../UI/Card";
import classes from "./EmployeesList.module.css";
import React, { useState, Fragment } from "react";
import EmployeeCard from "../../UI/EmployeeCard";

const EMPLOYEES = [
  {
    id: "m1",
    name: "Pavan",
    age: 24,
    department: "React.Js",
  },
  {
    id: "m2",
    name: "Babu",
    age: 24,
    department: "React.Js",
  },
  {
    id: "m3",
    name: "Bharath",
    age: 23,
    department: "React.Js",
  },
  {
    id: "m4",
    name: "Srikanth",
    age: 22,
    department: "Node.Js",
  },
  {
    id: "m5",
    name: "Venkat",
    age: 21,
    department: "Node.Js",
  },
  {
    id: "m6",
    name: "Siva",
    age: 21,
    department: "Node.Js",
  },
];

const Employee = (props) => {
  const [isVisible, setIsVisible] = useState(false);
  const [isVisibleAlert, setIsVisibleAlert] = useState(false);


  const nameAlertHandler = () => {
    alert(`Department: ${props.data.department}`);
  };
  const detailsHandler = () => {
    setIsVisible(true);
  };
  const alertShownHandler = () => {
    setIsVisibleAlert(true);
  };
  return (
    <Card>
      <div onClick={detailsHandler}>
        {!isVisibleAlert && <p  onClick={alertShownHandler}>
          <label onClick={nameAlertHandler}>
            Emplooyee Name: {props.data.name}
          </label>
        </p>}
      </div>

      {isVisible && (
        <Fragment>
          <p>
            <label>Emplooyee Id: {props.data.id}</label>
          </p>
          <p>
            <label>Emplooyee Age: {props.data.age}</label>
          </p>
          {/* <p>
            <label>Emplooyee Department: {props.data.department}</label>
          </p> */}
        </Fragment>
      )}
    </Card>
  );
};

const EmployeeDetails = (props) => {
  const empList = EMPLOYEES.map((emp) => (
    <Employee key={emp.id} data={emp}></Employee>
  ));
  return (
    <section className={classes.employees}>
      <EmployeeCard style={{backgroundColor: 'teal'}}>
        <ul>{empList}</ul>
      </EmployeeCard>
    </section>
  );
};

export default EmployeeDetails;
