import Card from "../../UI/Card";
import React, { useState, } from "react";
import EmployeeCard from "../../UI/EmployeeCard";

const EMPLOYEES = [
  {
    id: "B1",
    name: "Pavan",
    age: 24,
    department: "React.Js",
  },
  {
    id: "B2",
    name: "Babu",
    age: 24,
    department: "React.Js",
  },
  {
    id: "B3",
    name: "Bharath",
    age: 23,
    department: "React.Js",
  },
  {
    id: "B4",
    name: "Srikanth",
    age: 22,
    department: "Node.Js",
  },
  {
    id: "B5",
    name: "Venkat",
    age: 21,
    department: "Node.Js",
  },
  {
    id: "B6",
    name: "Siva",
    age: 21,
    department: "Node.Js",
  },
  {
    id: "B7",
    name: "Leela",
    age: 24,
    department: "React.Js",
  },
];

const Employee = (props) => {
  const [isVisible, setIsVisible] = useState(false);

  const nameAlertHandler = () => {
    alert(`Department: ${props.data.department}`);
  };
  const detailsHandler = () => {
    setIsVisible((prevState) => !prevState);
  };

  return (
    <Card>
      {!isVisible && (
        <div onClick={detailsHandler}>
          <h2>{props.data.name}</h2>
        </div>
      )}

      {isVisible && (
        <div onClick={detailsHandler}>
          <p>
            <label onClick={nameAlertHandler}>
              Emplooyee Name: {props.data.name}
            </label>
          </p>
          <p>
            <label>Emplooyee Id: {props.data.id}</label>
          </p>
          <p>
            <label>Emplooyee Age: {props.data.age}</label>
          </p>
          {/* <p>
            <label>Emplooyee Department: {props.data.department}</label>
          </p> */}
        </div>
      )}
    </Card>
  );
};

export default Employee;
