import {useEffect, useRef,useState} from "react";
const SimpleInput = (props) => {
  const nameInputRef=useRef();
  const [enteredInput, setEnterdInput]=useState();
  const [enterdInputIsValid, setEnterdInputIsValid]=useState(true);


  useEffect(()=>{
    if(enterdInputIsValid){
      console.log("Name Input Is Valid")
    }
  },[enterdInputIsValid])

  const nameInputChangeHandler=(event)=>{
       setEnterdInput(event.target.value);
  } ;
  const formSubmitHandler=(event)=>{
    event.preventDefault();

    if(enteredInput.trim() === ""){
      setEnterdInputIsValid(false);
      return;
    }
    setEnterdInputIsValid(true);

    console.log(enteredInput);
    setEnterdInput('');

  };

  const nameInputClasses=enterdInputIsValid ? 'form-control':'form-control invalid';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input 
        ref={nameInputRef}
        type = 'text' 
        id = 'name' 
        onChange={nameInputChangeHandler}
        />
      {!enterdInputIsValid && <p className="error-text">name is not be empty</p>}

      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
